import React, { useState, useEffect } from 'react';

const MyProfile = ({ user, jwtToken }) => {
  const [profileData, setProfileData] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  // Function to include the access token in requests
  const includeAccessToken = () => {
    const token = localStorage.getItem('access_token');
    return token ? `Bearer ${token}` : '';
  };
  useEffect(() => {
    if (!user || !jwtToken) {
      // Handle the case where user or jwtToken is not available
      return;
    }

    // Fetch user profile data based on the user's ID and role
    const apiUrl = `http://localhost:5000/${user.role === 'customer' ? 'customers' : 'moving_companies'}/${user.id}`;

    fetch(apiUrl, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': includeAccessToken(),
      }
    })
      .then((response) => response.json())
      .then((data) => {
        setProfileData(data); // Set the fetched profile data
      })
      .catch((error) => {
        console.error('Error fetching user profile:', error.message);
      });
  }, [user, jwtToken]);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  return (
    <div>
      <h2 onClick={toggleDetails}>My Profile</h2>
      {showDetails && (
        <div>
          <p>Username: {user.username}</p>
          <p>Email: {user.email}</p>
          <p>Role: {user.role}</p>
          {/* Conditionally render customer or moving company details */}
          {user.role === 'customer' && profileData ? (
            <div>
              <p>Full Name: {profileData.full_name}</p>
              <p>Contact Phone: {profileData.contact_phone}</p>
              <p>Address: {profileData.address}</p>
              <p>Preferred Contact Method: {profileData.preferred_contact_method}</p>
            </div>
          ) : null}
          {user.role === 'moving_company' && profileData ? (
            <div>
              <p>Company Name: {profileData.company_name}</p>
              <p>Contact Person: {profileData.contact_person}</p>
              <p>Contact Email: {profileData.contact_email}</p>
              <p>Contact Phone: {profileData.contact_phone}</p>
              <p>Extra Services: {profileData.extra_services}</p>
            </div>
          ) : null}
        </div>
      )}
    </div>
  );
};

export default MyProfile;
