import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  // State to manage form data
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  // State for handling loading state during API request
  const [loading, setLoading] = useState(false);

  // Access the navigate function for navigation
  const navigate = useNavigate();

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleLogin = async (e) => {
    e.preventDefault();

    // Disable the login button during API request
    setLoading(true);

    try {
      // Make a POST request to your login API endpoint
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      // Assuming your API returns a JSON object with a token upon successful login
      const data = await response.json();

      // Handle the token, for example, store it in local storage or state for authentication
      // ...

      // Redirect to the profile completion or dashboard page
      navigate('/complete-profile'); // Change this based on your routes
    } catch (error) {
      console.error('Login failed:', error);
      // Handle error, show error message, etc.
    } finally {
      // Enable the login button after API request is complete
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default Login;
