import React, { useState } from 'react';
import MyProfile from './MyProfile';
import Logout from './Logout';
import './Account.css'; // Import a CSS file for styling

const Account = () => {
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setDropdownVisible(false);
  };

  return (
    <div className="account-container">
      {isDropdownVisible && (
        <div className="dropdown-content">
          {selectedOption === 'profile' && <MyProfile />}
          {selectedOption === 'logout' && <Logout />}
        </div>
      )}

      <div className="account-dropdown" onClick={() => setDropdownVisible(!isDropdownVisible)}>
        {/* Add your styling for the dropdown trigger here if needed */}
      </div>

      {isDropdownVisible && (
        <div className="dropdown-options">
          <button onClick={() => handleOptionChange('profile')}>My Profile</button>
          <button onClick={() => handleOptionChange('logout')}>Logout</button>
        </div>
      )}
    </div>
  );
};

export default Account;
