import React, { useState } from 'react';
//import { useNavigate } from 'react-router-dom';

const Login = () => {
  //const navigate = useNavigate();
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });

  const handleInputChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      const result = await response.json();

      if (response.ok) {
        // Successful login
        console.log('Login successful');
        //navigate('/complete-profile'); // Redirect to the profile completion page
      } else {
        // Failed login
        console.error('Login failed:', result.error);
        // Handle the error, show a message to the user, etc.
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <label>Email:</label>
      <input type="email" name="email" value={loginData.email} onChange={handleInputChange} />
      <label>Password:</label>
      <input type="password" name="password" value={loginData.password} onChange={handleInputChange} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
