import React, { useState } from 'react';

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      const result = await response.json();

      if (response.ok) {
        // Successful login
        console.log('Login successful');

        // Check user role and redirect accordingly
        if (result.role === 'customer') {
          // Redirect to customer profile completion page
          console.log('Redirecting to customer profile completion');
          // Adjust the redirect based on your routing library
          // For example, using React Router: history.push('/complete-customer-profile');
        } else if (result.role === 'moving_company') {
          // Redirect to moving company profile completion page
          console.log('Redirecting to moving company profile completion');
          // Adjust the redirect based on your routing library
          // For example, using React Router: history.push('/complete-moving-company-profile');
        }
      } else {
        // Failed login
        setError(result.error || 'Login failed. Please check your credentials.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An unexpected error occurred. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <label>Email:</label>
      <input type="email" name="email" value={loginData.email} onChange={handleInputChange} />
      <label>Password:</label>
      <input type="password" name="password" value={loginData.password} onChange={handleInputChange} />
      <button onClick={handleLogin} disabled={loading}>
        {loading ? 'Logging In...' : 'Login'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default Login;
