import React, { useState } from 'react';
import Account from './Account';
import Inventory from './Inventory';
import Bookings from './Bookings';
import MovingPriceCalculator from './MovingPriceCalculator';
import MyProfile from './MyProfile'; // Import MyProfile component
import Logout from './Logout'; // Import Logout component
import './CustomerDashboard.css';

const CustomerDashboard = () => {
  const [selectedComponent, setSelectedComponent] = useState('account');
  const [showDropdown, setShowDropdown] = useState(false);

  const handleComponentChange = (component) => {
    setSelectedComponent(component);
    setShowDropdown(false); // Close the dropdown when a component is selected
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const renderDropdownContent = () => {
    if (showDropdown) {
      return (
        <div className="dropdown-content">
          <button onClick={() => handleComponentChange('profile')}>My Profile</button>
          <button onClick={() => handleComponentChange('logout')}>Logout</button>
        </div>
      );
    }
    return null;
  };

  const renderComponent = () => {
    switch (selectedComponent) {
      case 'inventory':
        return <Inventory />;
      case 'bookings':
        return <Bookings />;
      case 'movingPriceCalculator':
        return <MovingPriceCalculator />;
      case 'account':
        return (
          <div>
            <button onClick={toggleDropdown}>Account</button>
            {renderDropdownContent()}
          </div>
        );
      case 'profile':
        return <MyProfile />;
      case 'logout':
        return <Logout />;
      default:
        return null;
    }
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbars">
        <button onClick={() => handleComponentChange('inventory')}>Inventory</button>
        <button onClick={() => handleComponentChange('bookings')}>Bookings</button>
        <button onClick={() => handleComponentChange('movingPriceCalculator')}>
          Moving Price Calculator
        </button>
        {renderComponent()}
      </nav>

      {/* Welcome Message */}
      <h1>Welcome to the Customer Dashboard</h1>

      {/* Render the selected component */}
      {renderComponent()}
    </div>
  );
};

export default CustomerDashboard;
