import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const MovingPriceCalculator = () => {
  const location = useLocation();
  const { state } = location;
  const { startCity, endCity, distance } = state;

  const [quote, setQuote] = useState(null);

  useEffect(() => {
    // Call your quote calculation logic or API here
    calculateQuote();
  }, [startCity, endCity, distance]);

  const calculateQuote = () => {
    // Sample quote calculation logic (replace this with your actual logic)
    const baseRate = 0.1; // Adjust this based on your business logic
    const calculatedQuote = distance * baseRate;

    setQuote(calculatedQuote);
  };

  return (
    <div>
      <h2>Quote Calculator</h2>
      <p>
        Calculating quote for the distance between {startCity} and {endCity}: {distance} km.
      </p>

      {quote !== null && (
        <p>
          The calculated quote is: ${quote.toFixed(2)} {/* Format the quote as needed */}
        </p>
      )}
    </div>
  );
};

export default MovingPriceCalculator;
